      out.write("\r\n");
      out.write("\t\t$(\".submit\").click(function() {\r\n");
      out.write("\t\t\tvar leftDu = $(\"#LGD\").val();\r\n");
      out.write("\t\t\tvar leftSan = $(\"#LSG\").val();\r\n");
