      out.write("\t\t\t$(\".leftDu\").text(calculateDu(leftDu) + calculateSan(leftSan));\r\n");
      out.write("\t\t\t$(\".rightDu\").text(calculateDu(rightDu) + calculateSan(rightSan));\r\n");
      out.write("\t\t})\r\n");
      out.write("\r\n");
      out.write("\t\t//计算眼镜框架度数\r\n");
      out.write("\t\tfunction calculateDu(du) {\r\n");
      out.write("\t\t\tvar deg = du;\r\n");
      out.write("\t\t\tif (du >= 4.0 && du <= 5.25) {\r\n");
      out.write("\t\t\t\tdeg = du - 0.25;\r\n");
      out.write("\t\t\t} else if (du >= 5.50 && du <= 7.0) {\r\n");
      out.write("\t\t\t\tdeg = du - 5.0;\r\n");
      out.write("\t\t\t} else if (du >= 7.25 && du <= 8.50) {\r\n");
      out.write("\t\t\t\tdeg = du - 0.75;\r\n");
      out.write("\t\t\t} else if (du >= 8.75 && du <= 9.75) {\r\n");
      out.write("\t\t\t\tdeg = du - 1;\r\n");
      out.write("\t\t\t} else if (du >= 10.0 && du <= 11.0) {\r\n");
      out.write("\t\t\t\tdeg = du - 1.25;\r\n");
      out.write("\t\t\t} else if (du >= 11.25 && du <= 11.50) {\r\n");
      out.write("\t\t\t\tdeg = du - 1.5;\r\n");
      out.write("\t\t\t}\r\n");
      out.write("\t\t\treturn deg * 100;\r\n");
      out.write("\t\t}\r\n");
      out.write("\r\n");
      out.write("\t\t//计算散光\r\n");
      out.write("\t\tfunction calculateSan(san) {\r\n");
